div.script:
//

  var textAddName = "назначить имя";

  function showNameEditor(id) {
    switchElementVisibility(id, 'name');
    switchElementVisibility(id, 'editor');
    switchElementVisibility(id, 'closeeditor');
    var editor = $$('.b-savedsearch-editor-' + id)[0];
    try {
      editor.focus();
    }
    catch (e) {
    }
    return false;
  }

  function switchElementVisibility(id, postfix) {
    var element = $$('.b-savedsearch-' + postfix + '-' + id)[0];
    element.toggle();
  }

  function showElement(id, postfix) {
    var element = $$('.b-savedsearch-' + postfix + '-' + id)[0];
    element.show();
  }

  function hideElement(id, postfix) {
    var element = $$('.b-savedsearch-' + postfix + '-' + id)[0];
    element.hide();
  }

  function changeSavedSearchName(event, savedSearchId) {
    var key;
    if (window.event) {
      key = window.event.keyCode;
    }
    else {
      key = event.which;
    }

    if (key == 13) {
      saveSearchName(savedSearchId);
      return false;
    }
  }

  function saveSearchName(id) {
    var editor = $$('.b-savedsearch-editor-' + id)[0];
    var nameValue = editor.value;

    var changingBox = $$('.b-savedsearch-changing-' + id)[0];
    editor.hide();
    hideElement(id, 'closeeditor');
    changingBox.show();

    new Ajax.Request("/employer/savedSearches.do", {
      method: 'get',
      parameters: {
        resumeSavedSearchId: id,
        name: nameValue,
        changeNameAction: 'changeNameAction'
      },
      onSuccess: function() {
        var label = $$('.b-savedsearch-name-' + id)[0];
        label.innerHTML = nameValue.length > 0 ? nameValue.escapeHTML() : textAddName;
      },
      onFailure: function() {
      },
      onComplete: function() {
        editor.hide();
        changingBox.hide();
        showElement(id, 'name');
      }
    });
  }

